mapper.select:
select * from(
    select e.*,rownum r from(
        select USER_ID, USER_NAME, USER_SEX, USER_BIRTHDATE, USER_CERTNO, USER_ADDR, USER_ZIPCODE, USER_TELNO,
        USER_PHONENO, USER_EMAIL, USER_PWD, ACC_NO, CURR_TYPE, ACC_BANKID, ACC_STATUS, ACC_LIMIT, ACC_FUND, ACC_TYPE,
        ACC_DATE, ACC_PWD from CUSTOM



                        and user_name like concat('%',concat(#{query.qname},'%'))


                        and Acc_no=#{query.qAccNo}


                        and acc_status=#{query.qType}


                and rownum between 1 and 100

    ) e) where r>#{rowStart} and r<#{rowEnd}

select count(1) from CUSTOM


        and user_name like concat('%',concat(#{qname},'%'))


        and Acc_no=#{qAccNo}


        and acc_status=#{qType}

    and rownum between 1 and 100
