mapper.select:
select * from (select t.*,rownum r from (select * from T_PAYEE


        and debtor=#{debtor}


        and debtorName like concat('%',concat(#{debtorName},'%'))

    and creditorAcc=#{query.creditorAcc}

) t ) where r>#{rowStart} and r<=#{rowEnd}

select count(1) from T_PAYEE


        and debtor=#{debtor}


        and debtorName like concat('%',concat(#{debtorName},'%'))

    and creditorAcc=#{creditorAcc}
